for(var addCartBtn=document.querySelectorAll(".buy-btn"),popupCart=document.querySelector(".modal-cart"),cartClose=popupCart.querySelector(".modal-close"),i=0;i<addCartBtn.length;i++)addCartBtn[i].addEventListener("click",function(e){e.preventDefault(),popupCart.classList.add("modal--open")});cartClose.addEventListener("click",function(e){e.preventDefault(),popupCart.classList.remove("modal--open")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popupCart.classList.contains("modal--open")&&popupCart.classList.remove("modal--open"))});